#include<iostream>
#include<vector>
#include<unordered_set>
#include<queue>
using namespace std;

vector<vector<string>> findLadders(string beginWord, string endWord, vector<string>& wordList) {
        vector<string>usedOnLevel;
        queue<vector<string>>q;
        vector<vector<string>> ans;
        usedOnLevel.push_back(beginWord);
        unordered_set<string>st(wordList.begin(),wordList.end());

        q.push({beginWord});
        int level=0;
        while(!q.empty()){
            
                vector<string>vec=q.front();
                if(vec.size()>level){
                    level++;
                    for(auto it:usedOnLevel){  //erasing al the words that are used on the level
                        st.erase(it);
                    }
                }
                q.pop();
                string cw=vec.back();
                 if(cw==endWord){
                    if(ans.empty()){
                        ans.push_back(vec);  
                    }
                    else if(ans[0].size()==vec.size()){
                        ans.push_back(vec);
                    }
                                   
                }
                
              
                for(int j=0;j<cw.size();j++){
                      
                   
                    char original=cw[j];
                    for(char ch='a';ch<='z';ch++){
                        cw[j]=ch;
                        if(st.find(cw)!=st.end()){
                            vec.push_back(cw);
                            q.push(vec);
                            //mark as visited on the level
                            usedOnLevel.push_back(cw);
                            vec.pop_back();  //if next word is there which will not have the occur of cw
                        }
                        cw[j]=original;
                    }

                }
            
        }



        return ans;
        

    }

